package iterativeStatements;

public class DoWhileLoopexample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//the do-while loop where the condition is initially true and after few iterations has turned false (Demonstrate here)Loop will be iterated multiple times until the condition becomes false
		
				int i=0;
				
				do{
					
					System.out.println("Hello");
					
					i++;
					
				}while(i<3);
				
			}
		}
	


