package iterativeStatements;

public class WhileLoopExample3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
// the while loop where the condition is initially true and after few iterations is turned false.Loop will be executed until the condition turns false.
		
			
			int i=0;
			
			do{
				
				System.out.println("Hello");
				
				i++;
				
			}while(i<3);
			
		
	
	}

}
