package transferStatements;

public class BreakExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
//The purpose of the break; statement is to come out 
		//of the statements based on some condition.Demonstrate the usage of break; statements inside any loop statement		
		
		for(int i=0;i<5;i++){
			
			if(i==1)
				break;
			
			System.out.println("Hello-"+i);
			
		}
		
	}


	}


